export const initialState = {
    isActive: false
}

export const createReducer = (state = initialState, {type, payload}) =>{
    switch (state) {
        case 'IS_ACTIVE_TOGGLE' :
            return {
                ...state, isActive: true
            }
        default : return state
    }
}
